mapper.insert:
INSERT INTO SYS_LOGS (

  ,USER_ID
  ,USER_NAME
  ,DESCRIPTION
  ,CDATE
  ,IP
  ,LOCATION
  ,TYPE
  ,FUNCTION_ID
  ,FUNCTION_NAME

)  VALUES (

  ,#{userId,jdbcType=INTEGER}
  ,#{userName,jdbcType=VARCHAR}
  ,#{description,jdbcType=VARCHAR}
  ,#{cdate,jdbcType=TIMESTAMP}
  ,#{ip,jdbcType=VARCHAR}
  ,#{location,jdbcType=VARCHAR}
  ,#{type,jdbcType=CHAR}
  ,#{functionId,jdbcType=INTEGER}
  ,#{functionName,jdbcType=VARCHAR}

)
SELECT LAST_INSERT_ID() AS LOG_ID

mapper.select:
SELECT LOG_ID,USER_ID,USER_NAME,DESCRIPTION,CDATE,IP,LOCATION,TYPE,FUNCTION_ID,FUNCTION_NAME FROM SYS_LOGS

  AND LOG_ID=#{logId,jdbcType=INTEGER}
  AND USER_ID=#{userId,jdbcType=INTEGER}
  AND USER_NAME like '%${userName}%'
  AND USER_NAME=#{userNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CDATE=#{cdate,jdbcType=TIMESTAMP}
  AND CDATE>=#{cdateFrom,jdbcType=TIMESTAMP}
  AND CDATE<=#{cdateTo,jdbcType=TIMESTAMP}
  AND IP like '%${ip}%'
  AND IP=#{ipEq,jdbcType=VARCHAR}
  AND LOCATION like '%${location}%'
  AND LOCATION=#{locationEq,jdbcType=VARCHAR}
  AND TYPE like '%${type}%'
  AND TYPE=#{typeEq,jdbcType=CHAR}
  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}

SELECT count(1) as value FROM SYS_LOGS

  AND LOG_ID=#{logId,jdbcType=INTEGER}
  AND USER_ID=#{userId,jdbcType=INTEGER}
  AND USER_NAME like '%${userName}%'
  AND USER_NAME=#{userNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CDATE=#{cdate,jdbcType=TIMESTAMP}
  AND CDATE>=#{cdateFrom,jdbcType=TIMESTAMP}
  AND CDATE<=#{cdateTo,jdbcType=TIMESTAMP}
  AND IP like '%${ip}%'
  AND IP=#{ipEq,jdbcType=VARCHAR}
  AND LOCATION like '%${location}%'
  AND LOCATION=#{locationEq,jdbcType=VARCHAR}
  AND TYPE like '%${type}%'
  AND TYPE=#{typeEq,jdbcType=CHAR}
  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}

SELECT LOG_ID,USER_ID,USER_NAME,DESCRIPTION,CDATE,IP,LOCATION,TYPE,FUNCTION_ID,FUNCTION_NAME FROM SYS_LOGS

  AND LOG_ID=#{logId,jdbcType=INTEGER}
  AND USER_ID=#{userId,jdbcType=INTEGER}
  AND USER_NAME like '%${userName}%'
  AND USER_NAME=#{userNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CDATE=#{cdate,jdbcType=TIMESTAMP}
  AND CDATE>=#{cdateFrom,jdbcType=TIMESTAMP}
  AND CDATE<=#{cdateTo,jdbcType=TIMESTAMP}
  AND IP like '%${ip}%'
  AND IP=#{ipEq,jdbcType=VARCHAR}
  AND LOCATION like '%${location}%'
  AND LOCATION=#{locationEq,jdbcType=VARCHAR}
  AND TYPE like '%${type}%'
  AND TYPE=#{typeEq,jdbcType=CHAR}
  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}

 ORDER BY ${_orderBy}
