mapper.insert:
INSERT INTO SYS_NOTIFICATION (

  ,FUNCTION_NAME
  ,DESCRIPTION
  ,CREATE_DT
  ,IS_PROCEED
  ,PROCESS_USER_NAME
  ,PROCESS_USER_ID

)  VALUES (

  ,#{functionName,jdbcType=VARCHAR}
  ,#{description,jdbcType=VARCHAR}
  ,#{createDt,jdbcType=TIMESTAMP}
  ,#{isProceed,jdbcType=CHAR}
  ,#{processUserName,jdbcType=VARCHAR}
  ,#{processUserId,jdbcType=INTEGER}

)
SELECT LAST_INSERT_ID() AS ID

mapper.select:
SELECT ID,FUNCTION_NAME,DESCRIPTION,CREATE_DT,IS_PROCEED,PROCESS_USER_NAME,PROCESS_USER_ID FROM SYS_NOTIFICATION

  AND ID=#{id,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND IS_PROCEED=#{isProceed,jdbcType=CHAR}
  AND PROCESS_USER_NAME like '%${processUserName}%'
  AND PROCESS_USER_NAME=#{processUserNameEq,jdbcType=VARCHAR}
  AND PROCESS_USER_ID=#{processUserId,jdbcType=INTEGER}

SELECT count(1) as value FROM SYS_NOTIFICATION

  AND ID=#{id,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND IS_PROCEED=#{isProceed,jdbcType=CHAR}
  AND PROCESS_USER_NAME like '%${processUserName}%'
  AND PROCESS_USER_NAME=#{processUserNameEq,jdbcType=VARCHAR}
  AND PROCESS_USER_ID=#{processUserId,jdbcType=INTEGER}

SELECT ID,FUNCTION_NAME,DESCRIPTION,CREATE_DT,IS_PROCEED,PROCESS_USER_NAME,PROCESS_USER_ID FROM SYS_NOTIFICATION

  AND ID=#{id,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND DESCRIPTION like '%${description}%'
  AND DESCRIPTION=#{descriptionEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND IS_PROCEED=#{isProceed,jdbcType=CHAR}
  AND PROCESS_USER_NAME like '%${processUserName}%'
  AND PROCESS_USER_NAME=#{processUserNameEq,jdbcType=VARCHAR}
  AND PROCESS_USER_ID=#{processUserId,jdbcType=INTEGER}

 ORDER BY ${_orderBy}
