mapper.select:
SELECT din.code as parentCode,din.name as parentName,bf.`code` as childCode,bf.`name` as childName FROM bs_arearatio area INNER JOIN bs_area barea ON area.`area_id` = barea.`area_id` INNER JOIN bd_insurance din ON area.insurance_id = din.insurance_id INNER JOIN bd_insurancefields fs ON fs.insurance_id = din.insurance_id
INNER JOIN bd_businessfield bf ON bf.field_id = fs.field_id INNER JOIN ( SELECT tf.* from ( SELECT t.id,t.sp_id FROM ( SELECT template.* FROM sps_bill_temp_corp temcorp INNER JOIN sps_bill_template template ON temcorp.temp_id = template.id WHERE temcorp.cp_id = #{cmid} AND template.sp_id = #{spid} AND template.is_effect = 'Y' AND template.is_default = 'N' AND temcorp.dr = 0 AND template.dr = 0 UNION SELECT template.* FROM sps_bill_template template WHERE template.sp_id = #{spid} AND template.is_effect = 'Y' AND template.is_default = 'Y' AND template.dr = 0 ) t LIMIT 1) m INNER JOIN sps_bill_temp_business_field tf ON tf.temp_id = m.id AND tf.dr = 0
) lf ON lf.field_id = bf.field_id INNER JOIN ( SELECT ba. CODE, em.insurance_area FROM sps_bill_emp em INNER JOIN sps_bill re ON re.bill_id = em.bill_id AND re.sp_id =#{spid} AND re.cp_id = #{cmid} AND re.bill_id = #{bill_id} INNER JOIN cm_employee_insurance ins ON ins.emp_id = em.emp_id and ins.pay_type = 'MONTH' INNER JOIN bs_area ba ON em.`insurance_area` = ba.`area_id` GROUP BY em.insurance_area ) t ON LOCATE(barea.code, t.code) = 1 GROUP BY din.code,bf.code ORDER BY din.insurance_fund_type desc,din.name ASC,LEFT(bf.`code`,1) ASC,REPLACE(bf.`name`,'附加金额','比例') ASC

