#{item}
GROUP BY din.code,bf.code ORDER BY din.insurance_fund_type desc,din.name ASC,LEFT(bf.`code`,1) ASC,REPLACE(bf.`name`,'附加金额','比例') ASC

SELECT bf.`code` as parentCode,bf.`name` as parentName FROM bd_businessfield bf WHERE bf.field_id in

	#{item}

 AND  bf.source = 'INSURANCEFUND_SINGLE'

select t.insurance_id,t.round_method,b.`name`,b.insurance_fund_type
from bd_insurancearea t   inner join bd_insurance b
on t.insurance_id = b.insurance_id  where t.area_id = #{area_id}
 and b.insurance_fund_type = #{insuFundType}

select din.code as parentCode,din.name as parentName,bf.name as childName,bf.code as childCode from sps_account a inner JOIN sps_account_ratio r on a.acc_id=r.acc_id inner JOIN bs_arearatio ar on r.ratio_id=ar.ratio_id INNER JOIN bd_insurance din ON ar.insurance_id = din.insurance_id INNER JOIN bd_insurancefields fs ON fs.insurance_id = din.insurance_id INNER JOIN bd_businessfield bf ON bf.field_id = fs.field_id where a.cp_id=#{cpId}
and a.sp_id = #{spId}
and a.insurance_fund_type=#{insuFundType} AND din.insurance_id in(6,11) GROUP BY din.name,bf.name ORDER BY din.insurance_fund_type desc,din.name ASC,LEFT(bf.code,1) ASC,REPLACE(bf.name,'附加金额','比例') ASC
