mapper.insert:
  INSERT INTO CP_RELATION (

	,id
	,a_sp_id
	,b_sp_id
	,status
	,create_by
	,create_dt
	,modify_by
	,modify_dt
	,dr

  )  VALUES (

	,#{id}
	,#{aSpId}
	,#{bSpId}
	,#{status}
	,#{createBy}
	,#{createDt}
	,#{modifyBy}
	,#{modifyDt}
	,#{dr}

  )

	SELECT LAST_INSERT_ID() AS ID

mapper.select:
SELECT COUNT(1) AS VALUE FROM (SELECT csa.id, csa.status, csa.modify_dt, csa.spaName AS spaName, csa.spaMobile AS spaMobile, csb.name AS spbName, csb.mobile AS spbMobile, COUNT(co.cp_relation_id) AS task FROM ( select cr.id, cr.`status`, cr.modify_dt, cr.`a_sp_id`, cr.`b_sp_id`, cr.dr, cr.modify_dt AS spaModifyDt, csa.name AS spaName, csa.mobile AS spaMobile from cp_relation AS cr LEFT JOIN cp_service AS csa ON cr.a_sp_id = csa.saas_sp_id AND csa.dr = 0 GROUP BY cr.id ) csa LEFT JOIN cp_service AS csb ON csa.b_sp_id = csb.saas_sp_id AND csb.dr = 0 AND csb.isuse = 1 LEFT JOIN cp_order AS co ON csa.id = co.cp_relation_id AND co.dr = 0 AND co.stage > 1 AND csa.dr = 0
AND (csb.name like '%${spaName}%' or csb.mobile like '%${spaName}%') AND (csb.name LIKE '%${spbName}%' OR csb.mobile LIKE '%${spbName}%') AND spaModifyDt>=#{modifyDtFrom} AND spaModifyDt<=concat(substring(#{modifyDtTo},1,10),' ','23:59:59') AND csa.status = #{status} AND csa.id = #{id}
GROUP BY csa.id ORDER BY csa.modify_dt DESC)a

SELECT IFNULL(SUM(task),0) FROM (SELECT csa.id, csa.status, csa.modify_dt, csa.spaName AS spaName, csa.spaMobile AS spaMobile, csb.name AS spbName, csb.mobile AS spbMobile, COUNT(co.cp_relation_id) AS task FROM ( select cr.id, cr.`status`, cr.modify_dt, cr.`a_sp_id`, cr.`b_sp_id`, cr.dr, cr.modify_dt AS spaModifyDt, csa.name AS spaName, csa.mobile AS spaMobile from cp_relation AS cr LEFT JOIN cp_service AS csa ON cr.a_sp_id = csa.saas_sp_id AND csa.dr = 0 GROUP BY cr.id ) csa LEFT JOIN cp_service AS csb ON csa.b_sp_id = csb.saas_sp_id AND csb.dr = 0 AND csb.isuse = 1 LEFT JOIN cp_order AS co ON csa.id = co.cp_relation_id AND co.dr = 0 AND co.stage > 1 AND csa.dr = 0
AND (csb.name like '%${spaName}%' or csb.mobile like '%${spaName}%' OR spaName LIKE '%${spaName}%' OR spaMobile LIKE '%${spaName}%') AND spaModifyDt>=#{modifyDtFrom} AND spaModifyDt<=concat(substring(#{modifyDtTo},1,10),' ','23:59:59') AND csa.status = #{status} AND csa.id = #{id}
GROUP BY csa.id ORDER BY csa.modify_dt DESC) AS a
