mapper.insert:
INSERT INTO BL_RECEIPT_INFO (

	,id
	,trade_code
	,amount
	,receipt_time
	,account
	,account_name
	,deposit_bank
	,create_time
	,import_file_id
	,create_by
	,remain_amount
	,already_amount
	,dr
	,remark
	,modify_by
	,modify_dt
	,checkStatus

)  VALUES (

	,#{id}
	,#{tradeCode}
	,#{amount}
	,#{receiptTime}
	,#{account}
	,#{accountName}
	,#{depositBank}
	,#{createTime}
	,#{importFileId}
	,#{createBy}
	,#{remainAmount}
	,#{alreadyAmount}
	,#{dr}
	,#{remark}
	,#{modifyBy}
	,#{modifyDt}
	,#{checkStatus}

)

	SELECT LAST_INSERT_ID() AS ID

mapper.select:
	SELECT	FROM BL_RECEIPT_INFO

        and dr = 0
        and remain_amount >0 
        and account = #{account}
        and amount = #{amount}
        and deposit_bank = #{depositBank}
        and check_status = #{checkStatus}
        and receipt_time   >=    #{startTime}
        and receipt_time   <=    #{endTime}
        and account_name like #{accountName}

SELECT
FROM BL_RECEIPT_INFO WHERE dr = 0 and id in
#{item}

SELECT count(DISTINCT bri.id) from bl_receipt_info as bri
LEFT JOIN bl_receipt_write_off_info as brwoi  on bri.id=brwoi.receipt_id and brwoi.dr=0
LEFT JOIN bl_pay_voucher as bpv  on brwoi.order_code = bpv.id and bpv.dr=0
WHERE bri.amount > 0
and bri.check_status

	and bri.id = #{id}


	and bri.amount = #{amount}


	and bri.account = #{account}


	and bri.account_name = #{accountName}


	and bri.check_status = #{checkStatus}


	and DATE(bri.receipt_time) >= #{receiptTimeBegin}


	and DATE(bri.receipt_time) <= #{receiptTimeEnd}


	and bpv.bus_id = #{busId}

SELECT DISTINCT bri.id from bl_receipt_info as bri
LEFT JOIN bl_receipt_write_off_info as brwoi  on bri.id=brwoi.receipt_id and brwoi.dr=0
LEFT JOIN bl_pay_voucher as bpv  on brwoi.order_code = bpv.id and bpv.dr=0
WHERE bri.amount > 0
and bri.check_status

	and bri.id = #{id}


	and bri.amount = #{amount}


	and bri.account = #{account}


	and bri.account_name = #{accountName}


	and bri.check_status = #{checkStatus}


	and DATE(bri.receipt_time) >= #{receiptTimeBegin}


	and DATE(bri.receipt_time) <= #{receiptTimeEnd}


	and bpv.bus_id = #{busId}

order by bri.create_time desc limit #{offset}, #{pageSize}

select bri.id, bri.trade_code, bri.amount, bri.receipt_time, bri.account, bri.account_name, bri.deposit_bank, bri.remain_amount,bri.create_time,su_.user_name write_off_create_by, bri.already_amount, bri.remark, bri.check_status, brwo.write_off_type, bpv.buyer_name, bpv.payee_name, bus.app_name, su.user_name,brwo.create_time write_off_create_time, bpv.order_id,bpv.amount bpvAmount, bpv.already_check alreadyCheck, bpv.create_dt bpvCreateDt, bpv.status bpvStatus, bpv.channel_fee, bb.amount balanceAmount, brwo.write_off_amount, bpv.outer_trade_no, bpv.product_intro from bl_receipt_info bri left join bl_receipt_write_off_info brwo on bri.id = brwo.receipt_id left join bl_pay_voucher bpv on brwo.order_code = bpv.id left join bl_pay_business bus on bpv.bus_id = bus.bus_id left join sys_user su on bri.create_by = su.user_id left join bl_balance bb on bb.owner = bpv.buyer_id and bb.owner_type = buyer_type left join sys_user su_ on su_.user_id = brwo.create_by where 1=1 and bri.amount > 0
and bus.bus_id = #{busId}
and bri.id in
#{id}
order by bri.create_time desc

mapper.update:
UPDATE BL_RECEIPT_INFO

	,remain_amount=#{remainAmount}
	,already_amount=#{alreadyAmount}
	,modify_by=#{modifyBy}
	,modify_dt=#{modifyDt}
	,check_status=#{checkStatus}

WHERE ID=#{id}
and remain_amount is null 
and remain_amount=#{oldRemainAmount} 
and already_amount is null
and already_amount=#{oldAlreadyAmount}
and dr = 0
