mapper.insert:
  INSERT INTO SPS_ACCOUNT_RATIO (

	,id
	,acc_id
	,ratio_id
	,living_type
	,isdefault
	,create_by
	,create_dt
	,modify_by
	,modify_dt
	,dr

  )  VALUES (

	,#{id}
	,#{accId}
	,#{ratioId}
	,#{livingType}
	,#{isdefault}
	,#{createBy}
	,#{createDt}
	,#{modifyBy}
	,#{modifyDt}
	,#{dr}

  )

	SELECT LAST_INSERT_ID() AS ID

mapper.select:
		SELECT r.ratio_id,a.insurance_id,r.isdefault
		FROM SPS_ACCOUNT_RATIO r ,bs_arearatio a
		where r.acc_id=#{accId} 
		and r.ratio_id=a.ratio_id
		and r.living_type=#{livingType}
    	ORDER BY r.isdefault desc

update.if:
UPDATE
sps_account_ratio sr INNER JOIN (
SELECT t.ratio_id,acc.ratio_id as arid,acc.id FROM sps_account_ratio acc INNER JOIN bs_arearatio aratio ON acc.ratio_id = aratio.ratio_id AND acc.acc_id = #{accId} AND aratio.insurance_id = #{insuranceId} AND aratio.area_id = #{areaId} AND acc.dr = 0
INNER JOIN (
SELECT ratio.ratio_id FROM bs_arearatio ratio INNER JOIN bs_arearatiover ver ON ratio.ratio_id = ver.ratio_id  AND ratio.dr = 0 AND ratio.insurance_id = #{insuranceId} AND ratio.area_id = #{areaId}
AND DATE_FORMAT(NOW(),'%Y-%m') BETWEEN ver.begin_period AND IFNULL(ver.end_period,'2099-09') AND ver.corp_ratio = #{corpRatio}

	AND ver.psn_ratio = #{empRatio}

) t ) m ON sr.id = m.id SET sr.ratio_id = m.ratio_id

UPDATE
sps_account_ratio sr INNER JOIN (
SELECT t.living_type,t.ratio_id,acc.ratio_id as arid,acc.id FROM sps_account_ratio acc INNER JOIN bs_arearatio aratio ON acc.ratio_id = aratio.ratio_id AND acc.acc_id = #{accId} AND aratio.insurance_id = #{insuranceId} AND aratio.area_id = #{areaId} AND acc.dr = 0
INNER JOIN (
SELECT scope.living_type,ratio.ratio_id FROM bs_arearatio ratio INNER JOIN bs_arearatiover ver ON ratio.ratio_id = ver.ratio_id  AND ratio.dr = 0 AND ratio.insurance_id = #{insuranceId} AND ratio.area_id = #{areaId}
INNER JOIN
bs_arearatioscope scope ON scope.ratio_id = ratio.ratio_id AND DATE_FORMAT(NOW(),'%Y-%m') BETWEEN ver.begin_period AND IFNULL(ver.end_period,'2099-09') AND ver.corp_ratio = #{corpRatio}

	AND ver.psn_ratio = #{empRatio}

) t ON t.living_type = acc.living_type ) m ON sr.id = m.id SET sr.ratio_id = m.ratio_id
