mapper.insert:
  INSERT INTO SPS_SUPPLIER (

	,id
	,sp_id
	,supplier_name
	,type
	,create_by
	,create_dt
	,modify_by
	,modify_dt
	,dr

  )  VALUES (

	,#{id}
	,#{spId}
	,#{supplierName}
	,#{type}
	,#{createBy}
	,#{createDt}
	,#{modifyBy}
	,#{modifyDt}
	,#{dr}

  )

	SELECT LAST_INSERT_ID() AS ID

mapper.select:
select scheme.scheme_id,scheme.`name` as scheme_name, sp.sp_id,sp.sp_name,sp.contactor,sp.mobile,IFNULL(scheme.memo,"") memo, area.area_id,area.`name` as city, IFNULL(scheme.insurance_account_id,"") insuranceAccountId, IFNULL(scheme.fund_account_id,"") fundAccountId, scheme.bill_date,scheme.end_date,scheme.price, IFNULL(b.personNum,0) as personNum from sp_service sp INNER JOIN sps_scheme scheme on scheme.sp_id=sp.sp_id INNER JOIN bs_area area on area.area_id = scheme.area_id LEFT JOIN (select a1.scheme_id,count(1) as personNum from sps_scheme a1 LEFT JOIN sps_scheme_emp a2 on a1.scheme_id=a2.scheme_id LEFT JOIN cm_employee a3 on a3.emp_id=a2.emp_id where a1.cp_id=#{cpId} and a2.state='USE' and a2.dr=0 and a3.dr=0 and a1.sp_id!=-999 GROUP BY a1.scheme_id ) b on scheme.scheme_id=b.scheme_id where scheme.cp_id = #{cpId} and scheme.state='USE' and scheme.dr=0 and scheme.sp_id!=-999
and scheme.memo is not null
and scheme.scheme_id = #{schemeId}
and scheme.sp_id in (${spIds})
and scheme.sp_id = #{spId}
order by scheme.create_dt

select DISTINCT scheme.area_id,area.`name` from sp_service sp INNER JOIN sps_scheme scheme on scheme.sp_id=sp.sp_id INNER JOIN bs_area area on area.area_id = scheme.area_id where scheme.cp_id = #{cpId}
and scheme.sp_id = #{spId}
and scheme.state='USE' and scheme.dr=0 and scheme.sp_id!=-999
