mapper.select:
SELECT FUNCTION_ID,FUNCTION_NAME,URL,OTHERS,PRIORITY,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT,CATEGORY_ID FROM SYS_FUNCTION

  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND URL like '%${url}%'
  AND URL=#{urlEq,jdbcType=VARCHAR}
  AND OTHERS like '%${others}%'
  AND OTHERS=#{othersEq,jdbcType=VARCHAR}
  AND sys_code=#{sysCode,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND create_by = '%${createBy}%'
  AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND modify_by = '%${modifyBy}%'
  AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
  AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
  AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
  AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}

order by PRIORITY

SELECT count(1) as value FROM SYS_FUNCTION

  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND URL like '%${url}%'
  AND URL=#{urlEq,jdbcType=VARCHAR}
  AND OTHERS like '%${others}%'
  AND OTHERS=#{othersEq,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND create_by = '%${createBy}%'
  AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND modify_by = '%${modifyBy}%'
  AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
  AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
  AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
  AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}

SELECT FUNCTION_ID,FUNCTION_NAME,URL,OTHERS,PRIORITY,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT,CATEGORY_ID FROM SYS_FUNCTION

  AND FUNCTION_ID=#{functionId,jdbcType=INTEGER}
  AND FUNCTION_NAME like '%${functionName}%'
  AND FUNCTION_NAME=#{functionNameEq,jdbcType=VARCHAR}
  AND URL like '%${url}%'
  AND URL=#{urlEq,jdbcType=VARCHAR}
  AND OTHERS like '%${others}%'
  AND OTHERS=#{othersEq,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND create_by = '%${createBy}%'
  AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
  AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
  AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
  AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
  AND modify_by = '%${modifyBy}%'
  AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
  AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
  AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
  AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}

ORDER BY ${_orderBy}

SELECT FUNCTION_ID,FUNCTION_NAME,URL,OTHERS,PRIORITY,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT,CATEGORY_ID,sys_code FROM sys_function WHERE  category_id = 302
AND URL like '%${url}%'
ORDER  BY function_id DESC LIMIT 1;
