mapper.select:
SELECT CATEGORY_ID,CATEGORY_NAME,PRIORITY,PARENT_ID,ICON_CLASS FROM SYS_FUNCTION_CATEGORY

  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}
  AND CATEGORY_NAME like '%${categoryName}%'
  AND CATEGORY_NAME=#{categoryNameEq,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND PARENT_ID=#{parentId,jdbcType=INTEGER}
  AND ICON_CLASS like '%${iconClass}%'
  AND ICON_CLASS=#{iconClassEq,jdbcType=VARCHAR}

SELECT count(1) as value FROM SYS_FUNCTION_CATEGORY

  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}
  AND CATEGORY_NAME like '%${categoryName}%'
  AND CATEGORY_NAME=#{categoryNameEq,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND PARENT_ID=#{parentId,jdbcType=INTEGER}
  AND ICON_CLASS like '%${iconClass}%'
  AND ICON_CLASS=#{iconClassEq,jdbcType=VARCHAR}

SELECT CATEGORY_ID,CATEGORY_NAME,PRIORITY,PARENT_ID,ICON_CLASS FROM SYS_FUNCTION_CATEGORY

  AND CATEGORY_ID=#{categoryId,jdbcType=INTEGER}
  AND CATEGORY_NAME like '%${categoryName}%'
  AND CATEGORY_NAME=#{categoryNameEq,jdbcType=VARCHAR}
  AND PRIORITY=#{priority,jdbcType=INTEGER}
  AND PARENT_ID=#{parentId,jdbcType=INTEGER}
  AND ICON_CLASS like '%${iconClass}%'
  AND ICON_CLASS=#{iconClassEq,jdbcType=VARCHAR}

 ORDER BY ${_orderBy}

select distinct pa.category_id as category_id, pa.category_name as category_name, pa.priority, pa.icon_class from sys_function_category pa, sys_function f, sys_function_crud fc, sys_rel_role_func r where pa.category_id = f.category_id and f.function_id = fc.function_id and fc.rel_id = r.rel_id and r.role_id in
#{item}
order by pa.priority desc
