mapper.insert:
INSERT INTO SYS_REL_ROLE_FUNC (

  ,ROLE_ID
  ,REL_ID
  ,FUNCTION_CRUD_URL

)  VALUES (

  ,#{roleId,jdbcType=INTEGER}
  ,#{relId,jdbcType=INTEGER}
  ,#{functionCrudUrl,jdbcType=VARCHAR}

)
SELECT LAST_INSERT_ID() AS ROLE_FUNC_ID

mapper.select:
SELECT ROLE_FUNC_ID,ROLE_ID,REL_ID,FUNCTION_CRUD_URL FROM SYS_REL_ROLE_FUNC

  AND 1=2
  AND ROLE_ID=#{roleId,jdbcType=INTEGER}

SELECT ROLE_FUNC_ID,ROLE_ID,REL_ID,FUNCTION_CRUD_URL FROM SYS_REL_ROLE_FUNC

  AND ROLE_FUNC_ID=#{roleFuncId,jdbcType=INTEGER}
  AND ROLE_ID=#{roleId,jdbcType=INTEGER}
  AND REL_ID=#{relId,jdbcType=INTEGER}
  AND FUNCTION_CRUD_URL like '%${functionCrudUrl}%'
  AND FUNCTION_CRUD_URL=#{functionCrudUrlEq,jdbcType=VARCHAR}

SELECT count(1) as value FROM SYS_REL_ROLE_FUNC

  AND ROLE_FUNC_ID=#{roleFuncId,jdbcType=INTEGER}
  AND ROLE_ID=#{roleId,jdbcType=INTEGER}
  AND REL_ID=#{relId,jdbcType=INTEGER}
  AND FUNCTION_CRUD_URL like '%${functionCrudUrl}%'
  AND FUNCTION_CRUD_URL=#{functionCrudUrlEq,jdbcType=VARCHAR}

SELECT ROLE_FUNC_ID,ROLE_ID,REL_ID,FUNCTION_CRUD_URL FROM SYS_REL_ROLE_FUNC

  AND ROLE_FUNC_ID=#{roleFuncId,jdbcType=INTEGER}
  AND ROLE_ID=#{roleId,jdbcType=INTEGER}
  AND REL_ID=#{relId,jdbcType=INTEGER}
  AND FUNCTION_CRUD_URL like '%${functionCrudUrl}%'
  AND FUNCTION_CRUD_URL=#{functionCrudUrlEq,jdbcType=VARCHAR}

 ORDER BY ${_orderBy}
