mapper.insert:
INSERT INTO SYS_ROLE (

	,ROLE_NAME
	,ROLE_DESC
	,CREATE_BY
	,CREATE_DT
	,MODIFY_BY
	,MODIFY_DT
	,sys_code
	,role_group_code

)  VALUES (

	,#{roleName,jdbcType=VARCHAR}
	,#{roleDesc,jdbcType=VARCHAR}
	,#{createBy,jdbcType=VARCHAR}
	,#{createDt,jdbcType=TIMESTAMP}
	,#{modifyBy,jdbcType=VARCHAR}
	,#{modifyDt,jdbcType=TIMESTAMP}
	,#{sysCode,jdbcType=VARCHAR}
	,#{roleGroupCode,jdbcType=VARCHAR}

)
SELECT LAST_INSERT_ID() AS ROLE_ID

mapper.select:
SELECT ROLE_ID,ROLE_NAME,ROLE_DESC,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT FROM SYS_ROLE

	AND 1=2
	AND ROLE_ID=#{roleId,jdbcType=INTEGER}

SELECT ROLE_ID,ROLE_NAME,ROLE_DESC,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT FROM SYS_ROLE

	AND ROLE_ID=#{roleId,jdbcType=INTEGER}
	AND ROLE_NAME like '%${roleName}%'
	AND ROLE_NAME=#{roleNameEq,jdbcType=VARCHAR}
	AND ROLE_DESC like '%${roleDesc}%'
	AND ROLE_DESC=#{roleDescEq,jdbcType=VARCHAR}
	AND create_by = '%${createBy}%'
	AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
	AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
	AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
	AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
	AND modify_by = '%${modifyBy}%'
	AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
	AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
	AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
	AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
	AND sys_code=#{sysCode,jdbcType=VARCHAR}

SELECT count(1) as value FROM SYS_ROLE

	AND ROLE_ID=#{roleId,jdbcType=INTEGER}
	AND ROLE_NAME like '%${roleName}%'
	AND ROLE_NAME=#{roleNameEq,jdbcType=VARCHAR}
	AND ROLE_DESC like '%${roleDesc}%'
	AND ROLE_DESC=#{roleDescEq,jdbcType=VARCHAR}
	AND create_by = '%${createBy}%'
	AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
	AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
	AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
	AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
	AND modify_by = '%${modifyBy}%'
	AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
	AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
	AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
	AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
	AND sys_code=#{sysCode,jdbcType=VARCHAR}

SELECT ROLE_ID,ROLE_NAME,ROLE_DESC,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT FROM SYS_ROLE

	AND ROLE_ID=#{roleId,jdbcType=INTEGER}
	AND ROLE_NAME like '%${roleName}%'
	AND ROLE_NAME=#{roleNameEq,jdbcType=VARCHAR}
	AND ROLE_DESC like '%${roleDesc}%'
	AND ROLE_DESC=#{roleDescEq,jdbcType=VARCHAR}
	AND create_by = '%${createBy}%'
	AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
	AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
	AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
	AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
	AND modify_by = '%${modifyBy}%'
	AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
	AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
	AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
	AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
	AND sys_code<=#{sysCode,jdbcType=VARCHAR}

ORDER BY ${_orderBy}

SELECT ROLE_ID,ROLE_NAME,ROLE_DESC,CREATE_BY,CREATE_DT,MODIFY_BY,MODIFY_DT FROM SYS_ROLE

	AND ROLE_ID=#{roleId,jdbcType=INTEGER}
	AND ROLE_NAME like '%${roleName}%'
	AND ROLE_NAME=#{roleNameEq,jdbcType=VARCHAR}
	AND ROLE_DESC like '%${roleDesc}%'
	AND ROLE_DESC=#{roleDescEq,jdbcType=VARCHAR}
	AND create_by = '%${createBy}%'
	AND CREATE_BY=#{createByEq,jdbcType=VARCHAR}
	AND CREATE_DT=#{createDt,jdbcType=TIMESTAMP}
	AND CREATE_DT>=#{createDtFrom,jdbcType=TIMESTAMP}
	AND CREATE_DT<=#{createDtTo,jdbcType=TIMESTAMP}
	AND modify_by = '%${modifyBy}%'
	AND MODIFY_BY=#{modifyByEq,jdbcType=VARCHAR}
	AND MODIFY_DT=#{modifyDt,jdbcType=TIMESTAMP}
	AND MODIFY_DT>=#{modifyDtFrom,jdbcType=TIMESTAMP}
	AND MODIFY_DT<=#{modifyDtTo,jdbcType=TIMESTAMP}
	AND sys_code in (${sysCode})
